import React from "react";
import pokemons from '../data'

class Pokemon extends React.Component {
  render() {
    return (
      <section>
        <h2>{this.props.pokemons.name}</h2>
        <p>{this.props.pokemons.type}</p>
        <p>{this.props.pokemons.averageWeight.value} {this.props.pokemons.averageWeight.measurementUnit}</p>
        <img src={this.props.pokemons.image} alt={`imagem do ${this.props.pokemons.name}`} />
      </section>
    );
  }
}

export default Pokemon;
