import React from 'react';
import './App.css';
import Inputs from './components/Inputs';

class App extends React.Component {
  constructor () {
    super();

    this.state = {
      email: '',
    }
  }

  handleChange = ({target}) => {
    const { name, value } = target;
    this.setState({
      [name]: value,
    })
    console.log('to aqui')
  } 

  render() {
    const { email } = this.state;
    return (
      <div className="App">
        <h1>Formulário de dúvidas Revisão</h1>

        <Inputs
          inputLabel="E-mail:"
          type="email"
          name="email"
          handleChange={ this.handleChange }
          value={ email }
        />

        <Inputs
          inputLabel="Nome:"
          type=""
        />

      </div>
    );
  }  
}

export default App;
