import React from 'react';
import './App.css';
import Inputs from './components/Inputs';

class App extends React.Component {
  constructor () {
    super();

    this.state = {
      email: '',
    }
  }

  handleChange = ({target}) => {
    const { name, value } = target;
    this.setState({
      [name]: value,
    })
  } 

  render() {
    const { email } = this.state;
    return (
      <div className="App">
        <h1>Formulário de dúvidas Revisão</h1>

        <Inputs
        nomeDaProps={ this.handleChange }
        textoDaLabel="E-mail:"
        propsValue={ email }
        />
      </div>
    );
  }  
}

export default App;
