import React from "react";

class Inputs extends React.Component {

  render() {
    const { nomeDaProps, texto } = this.props;
    return (
      <label htmlFor="email">

        <input
          type="email"
          name="email"
          id="email"
          onChange={ nomeDaProps }

        />
      </label>

    )
  }
}

export default Inputs;
