import React from "react";

class Inputs extends React.Component {

  render() {
    const { handleChange, textoDaLabel, value, type, name, inputId } = this.props;
    return (
      <label htmlFor={ inputName }>
        { textoDaLabel }
        <input
          type={ type }
          name={ name }
          id={ name }
          onChange={ handleChange }
          value={ value } 

        />
      </label>


    )
  }
}

export default Inputs;
