import React from 'react';
import './App.css';
import Forms from './components/Form';

class App extends React.Component {
  constructor () {
    super();

    this.state = {
      email: '',
      password: '',
      showEmail: false,
      isRequired: true

    }
  }

  handleChange = ({target}) => {
    const { name, value } = target;
    this.setState({
      [name]: value,
      showEmail: false,
    })    
  }

  onSubmitForm = (e) => { 
    e.preventDefault();
    this.setState({
      showEmail: true,
    })
  }

  render() {
    const { email, password, showEmail, isRequired } = this.state;
    return (
      <div className="App">
        <h1>SUPER REVIS√ÉO</h1>

        <Forms
          email={ email }
          password={ password }
          handleChange={ this.handleChange }
          onSubmitForm={ this.onSubmitForm }
          isRequired={ isRequired }
        />
        <br />
        <br />

        { showEmail ? email : ''}



      </div>
    );
  }  
}

export default App;
