import React from 'react';
import './LoginForm.css';
import Inputs from './Inputs';
import Button from './Button';

class LoginForms extends React.Component {
  constructor () {
    super();

    this.state = {
      email: '',
      password: '',
      showEmail: false,
      isRequired: true,
      senha: 2903

    }
  }

  handleChange = ({target}) => {
    const { name, value } = target;
    this.setState({
      [name]: value,
      showEmail: false,
    })    
  }

  onSubmitForm = (e) => { 
    e.preventDefault();
    this.setState({
      showEmail: true,
    })
  }


  render() {
    // const { email, password, handleChange, onSubmitForm, isRequired } = this.props;
    const { email, password, showEmail, isRequired } = this.state;

    return (
      <form className="Border" onSubmit={ this.onSubmitForm }>
        <h2 className="ColorText" >Tela de Login</h2>
        <Inputs
          inputLabel="E-mail:"
          type="email"
          name="email"
          handleChange={ this.handleChange }
          value={ email }
        />

        <br />

        <Inputs
          inputLabel="Senha:"
          type="password"
          name="password"
          handleChange={ this.handleChange }
          value={ password }
          isRequired={ isRequired }
        />

        <br />
        <br />
        <br />

        <Button
          labelBtn="ENTRAR"
        />

        <br />

        { showEmail ? email : ''}

      </form>


    )
  }
}

export default LoginForms;
