import React from 'react';
import './Form.css';

class Form extends React.Component {
  constructor() {
    super();

    this.state = {
      estadoFavorito: '',
      nome: '',
      email: '',
      idade: 0,
      vaiComparecer: false,
      palavaraFavorita: '',
    }
  }

  handleChange = ({ target }) => {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;

    this.setState({
      [name]: value,
    });
  }

  render() {
    const { estadoFavorito, email, nome, idade } = this.state;
    return (
      <div>
        <h2>Estados React: ferramenta incrível ou reagindo a regionalismo?</h2>
        <form className="form">
          <label htmlFor="estadoFavorito">
            Diga qual é seu estado favorito! Do Brasil ou do React?!
            <textarea
              name="estadoFavorito"
              id="estadoFavorito"
              onChange={ this.handleChange }
              value={ estadoFavorito }
            />
          </label>

          <label htmlFor="email">
            Email
            <input
              type="email"
              name="email"
              id="email"
              onChange={ this.handleChange }
              value={ email }
            />
          </label>

          <label>
            Nome
            <input
              type="text"
              name=""
              onChange={ this.handleChange }
              value={ nome }

            />
          </label>

          <label>
            Idade
            <input
              type="number"
              onChange={ this.handleChange }
              value={ idade }
            />
          </label>

          <label>
            Vai comparecer?
            <input type="checkbox" />
          </label>

          <label>
            <input type="text" />
          </label>
        </form>
      </div>
    )
  }
}

export default Form;
