import React from 'react';
import './App.css'

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      numeroDeCliques1: 0,
      numeroDeCliques2: 0,
      numeroDeCliques3: 0,
    }

  }

  handleChange1 = () => {
    this.setState({
      numeroDeCliques1: 1,
    })
    console.log('cheguei 1')
  }
  handleChange2 = () => {
    this.setState({
      numeroDeCliques2: 1,
    })
    console.log('cheguei 2')
  }
  handleChange3 = () => {
    this.setState({
      numeroDeCliques3: 1,
    })
    console.log('cheguei 3')
  }


  render() {

    return (
      <div className="App-header">
        <div>
          <button onClick={ this.handleChange1 } >Meu Botão 1</button>  
          <button onClick={ this.handleChange2 } >Meu Botão 2</button>  
          <button onClick={ this.handleChange3 } >Meu Botão 3</button>  
        </div>
      </div>
    )
  }
}

export default App;
