import React from 'react';
import './App.css'

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      numeroDeCliques1: 0,
      numeroDeCliques2: 0,
      numeroDeCliques3: 0,
    }

  }

  handleChange1 = () => {
    const { numeroDeCliques1 } = this.state

    this.setState({
      numeroDeCliques1: numeroDeCliques1 + 1,
    })
    console.log('cheguei 1')
  }
  handleChange2 = () => {
    const { numeroDeCliques2 } = this.state

    this.setState({
      numeroDeCliques2: numeroDeCliques2 + 1,
    })
    console.log('cheguei 2')
  }
  handleChange3 = () => {

    this.setState((estadoAnterior, _props) => ({
      numeroDeCliques3: estadoAnterior + 1,
    }))
    console.log('cheguei 3')
  }


  render() {
    const { numeroDeCliques1, numeroDeCliques2, numeroDeCliques3 } = this.state;
    return (
      <div className="App-header">
        <div>
          <button onClick={ this.handleChange1 } >{ numeroDeCliques1 }</button>  
          <button onClick={ this.handleChange2 } >{ numeroDeCliques2 }</button>  
          <button onClick={ this.handleChange3 } >{ numeroDeCliques3 }</button>  
        </div>
      </div>
    )
  }
}

export default App;
