import React from 'react';

class Main extends React.Component {
  constructor() {
    super();
    this.state = {
      query: '',
    };

  }

  handlerInputQuery({target: { name, value} }) {
    this.setState({
      [name]: value,
    });
  }

  render() {
    const { query } = this.state;
    return (
      <div>
        <label>
          Digiste o termo da pesquisa:
          <input type='text'
            name='query'
            id='input-query'
            value={ query }
            onChange={ this.handlerInputQuery }
          />
        </label>
        <button>Pesquisar</button>
      </div>
    )
  }
}

export default Ma
