import React from 'react';

class Main extends React.Component {
  constructor() {
    super();
    this.state = {
      query: '',
      cocktails: null,
      loading: false,
    };

  }

  handlerInputQuery = ({ target: { name, value} }) => {
    this.setState({
      [name]: value,
    });
  }

  fetchData = () => {
    this.setState({
      loading: true,
      cocktails: null,
    }, () => {
      const { query, loading } = this.state;
    fetch(`www.thecocktaildb.com/api/json/v1/1/search.php?s=${query}`)
      .then((response) => response.json())
      .then((response) => {
        if (!response.drinks) {
          return this.setState({
            cocktails: response.json,
            loading: false,
          });
        }        
      // .catch((error) => console.error(error);)

    })})}

  render() {
    const { query, cocktails } = this.state;
    return (
      <div>
        <label htmlFor='input-query'>
          Digiste o termo da pesquisa:
          <input
            type='text'
            name='query'
            id='input-query'
            value={ query }
            onChange={ this.handlerInputQuery }
          />
        </label>
        <button type='button' onClick={ this.fetchData }>Pesquisar</button>
        {cocktails && cocktails.map((cocktail) => (
          <>
            <h1 key={ cocktail.idDrink }>{ cocktail.strDrink }</h1>
            <img src={ cocktail.strDrinkThum } alt />
          </>
        ))}
      </div>
    )
  }
}

export default Main;
